mapper.select:
SELECT

FROM sys_role a
WHERE a.name = #{name} AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT

FROM sys_role a
WHERE a.ename = #{ename} AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT , ro.org_id AS "orgList.id" FROM sys_role a LEFT JOIN sys_role_user ur ON ur.role_id = a.id LEFT JOIN sys_user u ON u.id = ur.user_id LEFT JOIN sys_role_org ro ON ro.role_id = a.id WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND u.id = #{user.id}
AND u.login_name = #{user.loginName}
ORDER BY a.name

mapper.insert:
INSERT INTO sys_role_menu(role_id, menu_id)

	SELECT #{id}, #{menu.id}

	FROM dual

INSERT INTO sys_role_org(role_id, org_id)

	SELECT #{id}, #{org.id}

		FROM dual

mapper.update:
UPDATE sys_role SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
