mapper.select:
SELECT

FROM sys_user a

WHERE a.id = #{id}

SELECT

FROM sys_user a

WHERE a.login_name = #{loginName} AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT
FROM sys_user a
INNER JOIN sys_role_user ur ON ur.user_id = a.id AND ur.role_id = #{role.id}
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND a.id != '1'
AND (d.id = #{employee.org.id} OR d.parent_ids LIKE '%'||#{employee.org.id}||'%') CONCAT('%', #{employee.org.id}, '%'))
AND (b.name LIKE '%'||#{employee.name}||'%' CONCAT('%', #{employee.name}, '%') OR b.ename like '%'||#{employee.name}||'%') CONCAT('%', #{employee.name}, '%'))
AND b.code LIKE '%'||#{employee.code}||'%' CONCAT('%', #{employee.code}, '%')
AND a.login_name like '%'||#{loginName}||'%' '%'+#{loginName}+'%' CONCAT('%', #{loginName}, '%')
AND a.name like '%'||#{name}||'%' '%'+#{name}+'%' CONCAT('%', #{name}, '%')
AND a.user_type = #{userType}
${sqlMap.dsf}
ORDER BY ${page.orderBy}
ORDER BY a.id

mapper.insert:
INSERT INTO sys_role_user(user_id, role_id)

	SELECT #{id}, #{role.id}

	FROM dual

mapper.update:
UPDATE sys_user SET
	password = #{password},
	update_by = #{updateBy.id},
	update_at = #{updateAt}
WHERE id = #{id}

UPDATE sys_user SET
	last_login_ip = #{lastLoginIp},
	last_login_at = #{lastLoginAt},
	this_login_ip = #{thisLoginIp},
	this_login_at = #{thisLoginAt}
WHERE id = #{id}

UPDATE sys_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
